package com.syntax.class09;

public class EnhacedLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		String[] colours= {"pink","blue","white","black","green"};
		for(String colour:colours) {System.out.println(colour+" ");} 
		//until colour has gone through all on the list of colours
		
		
		int[] numbers= {10,20,30,40};
		for(int num:numbers)
		{System.out.println(num);}		
		
		
		
		
		
		
	}

}
